-- If you do not have a Master Key on your DW you will need to create one
CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'medstarrfp@2022' ;

CREATE DATABASE SCOPED CREDENTIAL msi_cred 
WITH IDENTITY = 'Managed Service Identity' ;

CREATE EXTERNAL DATA SOURCE datalake 
WITH 
  (
    LOCATION = 'abfss://medstarrfpdatalakefs@medstarrfpdatalakeeastus.dfs.core.windows.net' , 
    CREDENTIAL = msi_cred
  ) ;  

CREATE EXTERNAL FILE FORMAT parquet_format  
WITH (  
  FORMAT_TYPE = PARQUET,  
  DATA_COMPRESSION =  'org.apache.hadoop.io.compress.GzipCodec'
); 

CREATE EXTERNAL FILE FORMAT json_format  
WITH (  
    FORMAT_TYPE = JSON,  
    DATA_COMPRESSION =  'org.apache.hadoop.io.compress.GzipCodec'
);

CREATE EXTERNAL FILE FORMAT comma_delimit_format  
WITH (  
    FORMAT_TYPE = DELIMITEDTEXT,  
    FORMAT_OPTIONS (  
        FIELD_TERMINATOR = ',',  
        DATE_FORMAT = 'MM/dd/yyyy' ),  
    DATA_COMPRESSION = 'org.apache.hadoop.io.compress.GzipCodec'
);  

CREATE EXTERNAL TABLE [dbo].[provider]
( [NPI] [int]  NULL,
  [ProviderName] nvarchar NULL,
  [ProviderPrimarySpeciality] nvarchar NULL )
WITH
(
    LOCATION='/providers/raw/true/' ,
    DATA_SOURCE = datalake,
    FILE_FORMAT = parquet_format
) ;
